CREATE TABLE IF NOT EXISTS pw_category (id INT NOT NULL AUTO_INCREMENT PRIMARY KEY, name VARCHAR(50) UNIQUE);
CREATE TABLE IF NOT EXISTS pw_user (id INT AUTO_INCREMENT PRIMARY KEY, login VARCHAR(30) UNIQUE, password_user VARCHAR(32), role INT);
CREATE TABLE IF NOT EXISTS pw_log (id INT AUTO_INCREMENT PRIMARY KEY, login VARCHAR(30), ip VARCHAR(15), date_log DATE, time_log TIME, status_log TINYINT);
CREATE TABLE IF NOT EXISTS pw_product (id INT AUTO_INCREMENT PRIMARY KEY, name VARCHAR(30), count_product INT, category_id INT, FOREIGN KEY (category_id) REFERENCES pw_category(id) ON DELETE CASCADE);